<template>
  <div v-if="hasQuizzesQuestions">
    <base-dialog
      :show="!!error"
      title="Fetch Error"
      @close="handleError"
    >{{ error }}
    </base-dialog>
    <section class="quiz-section quiz-questions-section">
      <base-heading :heading="quizName" base/>
      <div class="card-subtitle-container">
        <base-widget
          :value="questionsNumber"
          icon="data:image/png;base64,"
          label="Questions"/>
        <base-widget
          :value="level"
          badge
          icon="data:image/png;base64,"
          label="Level">
          <base-badge :mode="badgeClass">{{ level }}</base-badge>
        </base-widget>
      </div>
      <base-card>
        <base-spinner v-if="isLoading"/>
        <ul v-else-if="hasQuizzesQuestions" class="table">
          <quiz-quizzes-questions-item v-for="(question, index) in quizzesQuestions"
                                       :key="question.creationTime"
                                       :creationTime="question.creationTime"
                                       :index="index"
                                       :name="question.name"
                                       :quizName="question.quiz.name"
          ></quiz-quizzes-questions-item>
        </ul>
        <base-card v-else>
          <div class="item">No quiz questions found.</div>
        </base-card>
      </base-card>
    </section>
  </div>
</template>

<script>
import ErrorMixin from '@/mixins/ErrorMixin'
import QuizQuizzesQuestionsItem from '@/components/quiz/QuizQuizzesQuestionsItem'
import QuizMixin from '@/mixins/QuizMixin'

export default {
  name: 'QuizQuizzesQuestionsList',
  components: {
    QuizQuizzesQuestionsItem
  },
  mixins: [ErrorMixin, QuizMixin],
  computed: {
    id() {
      return this.$route.params.id
    },
    quizzesQuestions() {
      return this.$store.getters['quiz/quizzesQuestions']
    },
    hasQuizzesQuestions() {
      return this.$store.getters['quiz/hasQuizzesQuestions']
    },
    quizName() {
      return this.quizzesQuestions[0].quiz.name
    },
    questionsNumber() {
      return this.quizzesQuestions.length
    },
    level() {
      return this.quizzesQuestions[0].quiz.level
    },
    badgeClass(){
      return [`${this.gameLevel} quiz-parameter-value`]
    }
  },
  methods: {
    async loadQuizzesQuestions() {
      this.isLoading = true
      try {
        await this.$store.dispatch('quiz/fetchQuizzesQuestions', this.id)
      } catch (error) {
        this.error = error.message || 'Something went wrong while fetching quizzes questions!'
      }
      this.isLoading = false
    }
  },
    created() {
      this.loadQuizzesQuestions(this.id)
    }
}
</script>

<style lang="scss" scoped>
.link {
  position: absolute;
}

.table {
  line-height: 25px;
}

</style>
